SELECT ord.*, pro.name as productName, pro.price as productPrice FROM `orders` ord LEFT JOIN product pro on ord.product_id = pro.id and pro.expire_date is null
1=1 and ord.expire_date is null
and ord.user_id = #{userId}
and ord.user_sales_id = #{userSalesId}
and pro.name like concat('%', #{productName}, '%')
and (ord.customer_name like concat('%', #{customer}, '%') or ord.customer_mobile like concat('%', #{customer}, '%'))
and ord.create_date >= #{startDate}
and #{endDate} >= ord.create_date
order by ord.create_date desc

SELECT ord.*, pro.name as productName, pro.price as productPrice FROM `orders` ord
LEFT JOIN product pro on ord.product_id = pro.id

    1=1
    and ord.expire_date is not null

        and ord.id = #{id}


